<figure class="highlight"><pre><code class="language-ruby" data-lang="ruby"><span class="n">header</span><span class="p">(</span><span class="s1">'content-type:text/html;charset=utf-8'</span><span class="p">);</span>    
<span class="n">function</span> <span class="n">curlPost</span><span class="p">(</span><span class="vg">$url</span><span class="p">,</span><span class="vg">$data</span><span class="p">,</span><span class="vg">$method</span><span class="p">){</span>    
    <span class="vg">$ch</span> <span class="o">=</span> <span class="n">curl_init</span><span class="p">();</span>   <span class="sr">//</span><span class="mi">1</span><span class="o">.</span><span class="err">初始化</span>    
    <span class="n">curl_setopt</span><span class="p">(</span><span class="vg">$ch</span><span class="p">,</span> <span class="no">CURLOPT_URL</span><span class="p">,</span> <span class="vg">$url</span><span class="p">);</span> <span class="sr">//</span><span class="mi">2</span><span class="o">.</span><span class="err">请求地址</span>    
    <span class="n">curl_setopt</span><span class="p">(</span><span class="vg">$ch</span><span class="p">,</span> <span class="no">CURLOPT_CUSTOMREQUEST</span><span class="p">,</span> <span class="vg">$method</span><span class="p">);</span><span class="sr">//</span><span class="mi">3</span><span class="o">.</span><span class="err">请求方式</span>    
    <span class="sr">//</span><span class="mi">4</span><span class="o">.</span><span class="err">参数如下</span>    
    <span class="n">curl_setopt</span><span class="p">(</span><span class="vg">$ch</span><span class="p">,</span> <span class="no">CURLOPT_SSL_VERIFYPEER</span><span class="p">,</span> <span class="no">FALSE</span><span class="p">);</span><span class="sr">//</span><span class="n">https</span>    
    <span class="n">curl_setopt</span><span class="p">(</span><span class="vg">$ch</span><span class="p">,</span> <span class="no">CURLOPT_SSL_VERIFYHOST</span><span class="p">,</span> <span class="no">FALSE</span><span class="p">);</span>    
    <span class="n">curl_setopt</span><span class="p">(</span><span class="vg">$ch</span><span class="p">,</span> <span class="no">CURLOPT_USERAGENT</span><span class="p">,</span> <span class="s1">'Mozilla/5.0 (compatible; MSIE 5.01; Windows NT 5.0)'</span><span class="p">);</span><span class="sr">//</span><span class="err">模拟浏览器</span>    
    <span class="n">curl_setopt</span><span class="p">(</span><span class="vg">$ch</span><span class="p">,</span> <span class="no">CURLOPT_FOLLOWLOCATION</span><span class="p">,</span> <span class="mi">1</span><span class="p">);</span>    
    <span class="n">curl_setopt</span><span class="p">(</span><span class="vg">$ch</span><span class="p">,</span> <span class="no">CURLOPT_AUTOREFERER</span><span class="p">,</span> <span class="mi">1</span><span class="p">);</span>    
        <span class="n">curl_setopt</span><span class="p">(</span><span class="vg">$ch</span><span class="p">,</span> <span class="no">CURLOPT_HTTPHEADER</span><span class="p">,</span><span class="n">array</span><span class="p">(</span><span class="s1">'Accept-Encoding: gzip, deflate'</span><span class="p">));</span><span class="sr">//</span><span class="n">gzip</span><span class="err">解压内容</span>    
        <span class="n">curl_setopt</span><span class="p">(</span><span class="vg">$ch</span><span class="p">,</span> <span class="no">CURLOPT_ENCODING</span><span class="p">,</span> <span class="s1">'gzip,deflate'</span><span class="p">);</span>    

    <span class="k">if</span><span class="p">(</span><span class="vg">$method</span><span class="o">==</span><span class="s2">"POST"</span><span class="p">){</span><span class="sr">//</span><span class="mi">5</span><span class="p">.</span><span class="nf">post</span><span class="err">方式的时候添加数据</span>    
        <span class="n">curl_setopt</span><span class="p">(</span><span class="vg">$ch</span><span class="p">,</span> <span class="no">CURLOPT_POSTFIELDS</span><span class="p">,</span> <span class="vg">$data</span><span class="p">);</span>    
    <span class="p">}</span>    
    <span class="n">curl_setopt</span><span class="p">(</span><span class="vg">$ch</span><span class="p">,</span> <span class="no">CURLOPT_RETURNTRANSFER</span><span class="p">,</span> <span class="kp">true</span><span class="p">);</span>    
    <span class="vg">$tmpInfo</span> <span class="o">=</span> <span class="n">curl_exec</span><span class="p">(</span><span class="vg">$ch</span><span class="p">);</span><span class="sr">//</span><span class="mi">6</span><span class="o">.</span><span class="err">执行</span>    

    <span class="k">if</span> <span class="p">(</span><span class="n">curl_errno</span><span class="p">(</span><span class="vg">$ch</span><span class="p">))</span> <span class="p">{</span><span class="sr">//</span><span class="mi">7</span><span class="o">.</span><span class="err">如果出错</span>    
