<span class="vg">$file</span><span class="o">=</span><span class="n">mb_convert_encoding</span><span class="p">(</span><span class="vg">$file</span><span class="p">,</span><span class="s1">'UTF-8'</span><span class="p">,</span><span class="s1">'GBK'</span><span class="p">);</span>    
